import React,{useState} from 'react';
import {Table} from 'react-bootstrap';
import {useTable, useBlockLayout, useGlobalFilter, useSortBy} from 'react-table';
// import {GlobalFilter} from '../GlobalFilter';
import scrollbarWidth from './scrollbarWidth';
import { FixedSizeList } from 'react-window';

function GlobalFilter(){
  return (<input value={"Hello"}/>);
}

export function TableV2({ columns, data }){



  const defaultColumn = React.useMemo(
    () => ({
      width: 150,
    }),
    []
  )

  const scrollBarSize = React.useMemo(() => scrollbarWidth(), [])

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    totalColumnsWidth,
    prepareRow,
    state: { globalFilter },
    visibleColumns,
    preGlobalFilteredRows,
    setGlobalFilter
  } = useTable({ columns, data, defaultColumn },useBlockLayout, useGlobalFilter, useSortBy);


  const RenderRow = React.useCallback(
    ({ index, style }) => {
      const row = rows[index]
      prepareRow(row)
      return (
        <tr
          {...row.getRowProps({
            style,
          })}
          className="tr"
        >
          {row.cells.map(cell => {
            return (
              <td {...cell.getCellProps()} className="td">
                {cell.render('Cell')}
              </td>
            )
          })}
        </tr>
      )
    },
    [prepareRow, rows]
  )

      return (
        <div>
        <Table {...getTableProps()} striped bordered hover>
          <thead>
          <tr>
          <th
            colSpan={visibleColumns.length}
          >
            <GlobalFilter

            />
          </th>
        </tr>
            {headerGroups.map(headerGroup => (
              <tr {...headerGroup.getHeaderGroupProps()} >
                {headerGroup.headers.map(column => (
                  <th {...column.getHeaderProps()} >
                    {column.render('Header')}
                  </th>
                ))}
              </tr>
            ))}
          </thead>

          <tbody {...getTableBodyProps()}>
            <FixedSizeList
              height={500}
              itemCount={rows.length}
              itemSize={45}
              width={totalColumnsWidth+scrollBarSize}
            >
              {RenderRow}
            </FixedSizeList>
          </tbody>
        </Table>
        </div>
      )
}
